"""Determine whether an integer is a palindrome. Do this without extra space."""class Solution:    def isPalindrome(self, x):        """        :type x: int        :rtype: bool        """        if x < 0: return False        if x == 0: return True        if x % 10 == 0: return False        reversedNum = 0        xtemp = 0        while reversedNum < x:            reversedNum = reversedNum * 10 + x % 10            xtemp = x            x = int(x / 10)        if reversedNum == x or reversedNum == xtemp:            return True        return False