# coding:utf-8import itertoolsimport time# each number below a pair of numbers is the positive difference of them. Such as:# 3 1# 2start = time.time()Num = 5Sum = int(Num * (Num + 1) / 2)a = itertools.permutations(list(range(1, 1 + Sum)), Num)for l in a:    NewList = list(l)    last = NewList    for i in range(Num - 1):        ToAppend = [abs(last[k] - last[k + 1]) for k in range(len(last) - 1)]        NewList = NewList + ToAppend        if len(NewList) != len(set(NewList)):            break        if 0 in NewList:            break        last = ToAppend        if i == Num - 2:            print(NewList)print(time.time() - start)